fn main() {
    println!("{}", my_first_interpreter("+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++.++++++++++++++..."))
}

fn my_first_interpreter(code: &str) -> String {
    let mut res = String::new();
    let mut i: u8 = 0;
    for c in code.chars() {
        match c {
            '+' => i = i.wrapping_add(1),
            '.' => res.push(i as char),
            _ => (),
        }
    }
    res
}

#[test]
fn test0() {
    assert_eq!(my_first_interpreter("+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++.+.+.+.+.+.+.+.+.+.+.+.+.+.+.+.+.+.+.+.+.+.+.+.+.+."), "ABCDEFGHIJKLMNOPQRSTUVWXYZ");
}

